
const main_menu=document.getElementById("menu-main-menu");console.log("Main Menu has sub menu Count ::: "+main_menu.getElementsByClassName("sub-menu").length);console.log("Main Menu has menu-item Count ::: "+main_menu.getElementsByClassName("menu-item").length);const target_parent=document.getElementById("menu-item-113");const target_parent1=document.getElementById("menu-item-112");const target_parent2=document.getElementById("menu-item-116");const target_parent3=document.getElementById("menu-item-117");const target_parent4=document.getElementById("menu-item-118");const target_child=target_parent.getElementsByClassName("sub-menu")[0];target_parent.addEventListener('mouseenter',()=>{target_child.classList.add("sub-menu-visible");},false);target_child.addEventListener('mouseleave',()=>{target_child.classList.remove("sub-menu-visible");},false);target_parent1.addEventListener('mouseenter',()=>{target_child.classList.remove("sub-menu-visible");},false);target_parent2.addEventListener('mouseenter',()=>{target_child.classList.remove("sub-menu-visible");},false);target_parent3.addEventListener('mouseenter',()=>{target_child.classList.remove("sub-menu-visible");},false);target_parent4.addEventListener('mouseenter',()=>{target_child.classList.remove("sub-menu-visible");},false);document.addEventListener('mousemove',parallax);function parallax(e){this.querySelectorAll('.wpba-heroimage-layer').forEach(layer=>{let speed=layer.getAttribute('data-speed');let x=(window.innerWidth-e.pageX*speed)/100;let y=(window.innerWidth-e.pageY*speed)/100;layer.style.transform=`translate(${x}px,${y}px)`;});}